// function return an action object which is returned to the reducer to update 

export const fetchTeas = () => {

    return dispatch => {
      return fetch("http://localhost:3000/api/v1/teas")
        .then(resp => resp.json())
        .then(teasJSON => {
          if (teasJSON.error) {
            alert(teasJSON.error)
          } else {
            dispatch(loadTeas(teasJSON))
          }
        })
    }
  }
