import React from 'react'
import {connect} from 'react-redux'
import { createTeas } from '../actions/teasFetch'
import { FormControl } from 'react-bootstrap'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

// const initialState = {
//     name: "",
//     brand: "",
//     price: "",
//     img_url: "",
//     description: "",

// }

class TeasInput extends React.Component{
    state = {
        name: "",
        brand: "",
        price: "",
        img_url: "",
        description: "",
    }
handleChange = (event) => {
    this.setState({ 
        [event.target.name]: event.target.value
    })
}
handleSubmit = (event) =>{
        event.preventDefault()
        this.props.createTeas(this.state)
        this.setState({
            name: "",
            brand: "",
            price: "",
            img_url: "",
            description: "",
        })
}

    render(){
        return(
            <div>
                <form inline onSubmit={this.handleSubmit}>
                <FormControl type="text" name="name" placeholder="Name" className="mr-sm-2" onChange={this.handleChange} value={this.state.name}/><br/>
                <FormControl type="text" name="brand" placeholder="brand" className="mr-sm-2" onChange={this.handleChange} value={this.state.brand}/><br/>
                <FormControl type="text" name="description" placeholder="description" className="mr-sm-2" onChange={this.handleChange} value={this.state.description}/><br/>
                <FormControl type="text" name="img_url" placeholder="img_url" className="mr-sm-2" onChange={this.handleChange} value={this.state.img_url}/><br/>
                <FormControl type="text" name="price" placeholder="price" onChange={this.handleChange} value={this.state.price} /><br/>
                <FormControl type="text" type="submit" value="Add Tea"/>

                </form>
            </div>
        )
    }
}//controlled form

export default connect (null, {createTeas})(TeasInput)
