import React from 'react'
import {connect} from 'react-redux'


class ReviewsInput extends React.Component{
state ={
    comment: ''
}

handleChange = (event) => {
    this.setState({
        [event.target.name]: event.target.value
    })

}


render (){
return(
    <div>
        <form>
            <label>Write your Comment:
            <input type='text' placeholder='Comment' value={this.state.name} name='comment'/>
            </label>
            <input type="submit" value="Add "/>
        </form>

    </div>
)
}


}

export default connect(null)(ReviewsInput)
