import React from 'react'
import Tea from './Tea'

//functional presenting => render the list of teas
//props are passed in as an arguement in functional component
 const Teas = (props)=> {
    //  console.log(props)
     return(
    <div>
       {props.teas.map(tea => 
        <div key={tea.id}> {tea.name} - {tea.brand}-{tea.description}</div>)}

         </div>
     )


 }

 export default Teas


